      //    status: 500,
      //  })
       //)

    return json(
      {Message: 'could not fetch event'},
      {status: 500 ,}
    )
}
  else{
    return response;
    // const resData = await response.json();
    // return resData.events;
  }
}


export  function loader(){
  return  defer({
          events: loadEvents()
        })
}
