import React from 'react';
import EventItem from '../components/EventItem';
import {userLoaderData,json} from 'react-router-dom';

const EventDetailPage = () => {

  const data = userLoaderData()
  return (
    <EventItem event={data}/>
  );
}

export default EventDetailPage;


export async function loader({request,params}){
  const id= params.eventId;
  const response = fetch('http://localhost:8080/events/' +id);

  if(!response.ok){
    return json({message: 'couls not fetch details for selected event.'})
    ({status:500})
  }
  else{
    return response;
  }
}
